import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getApplicationById, updateApplicationStatus, downloadFile } from '../services/api';
import '../styles/Buttons.css';
import '../styles/Form.css';
import '../styles/App.css'; // For workflow styles

const detailContainerStyle = {
  maxWidth: '800px',
  margin: '2rem auto',
};

const detailStyle = {
  padding: '2rem',
  background: '#fff',
  borderRadius: '8px',
  boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
};

const sectionStyle = {
  marginBottom: '2rem',
};

const fileListStyle = {
  listStyle: 'none',
  padding: 0,
};

const fileListItemStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '10px',
  borderBottom: '1px solid #eee',
};

const formatStatus = (status) => {
  if (!status) return 'N/A';
  return status
    .toLowerCase()
    .replace(/_/g, ' ')
    .replace(/(?: |\b)(\w)/g, (char) => char.toUpperCase());
};

const STAGES = [
  'DRAFTING_AND_FILING',
  'FORMALITY_EXAMINATION',
  'APPLICATION_PUBLICATION',
  'SUBSTANTIVE_EXAMINATION_REQUEST',
  'SUBSTANTIVE_EXAMINATION',
  'FEE_PAYMENT_AND_GRANT',
];

const ApplicationDetail = ({ user }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [application, setApplication] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isAuthorized, setIsAuthorized] = useState(false);
  const [rejectionReason, setRejectionReason] = useState('');
  const [statusChangeError, setStatusChangeError] = useState('');

  useEffect(() => {
    const fetchApplication = async () => {
      try {
        const response = await getApplicationById(id);
        const fetchedApp = response.data;
        setApplication(fetchedApp);

        if (user.role === 'REVIEWER' || (user.role === 'USER' && fetchedApp.submittedByUsername === user.username)) {
          setIsAuthorized(true);
        }
      } catch (err) {
        setError('Failed to fetch application details.');
      } finally {
        setLoading(false);
      }
    };
    if (user) fetchApplication();
  }, [id, user]);

  const handleStatusUpdate = async (newStatus, reason = '') => {
    setStatusChangeError('');
    try {
      const response = await updateApplicationStatus(id, { status: newStatus, rejectionReason: reason });
      setApplication(response.data);
      setRejectionReason('');
    } catch (err) {
      setStatusChangeError(`Failed to update status: ${err.response?.data?.message || err.message}`);
    }
  };

  const handleFileDownload = async (file) => {
    try {
      await downloadFile(file.id, file.fileName);
    } catch (err) {
      setError(`Failed to download file ${file.fileName}.`);
    }
  };

  if (loading) return <p>Loading application details...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!isAuthorized) return <p style={{ color: 'red' }}>You are not authorized to view this application.</p>;
  if (!application) return <p>No application found.</p>;

  const isReviewer = user.role === 'REVIEWER';
  const isOwner = user.username === application.submittedByUsername;
  const currentStatusIndex = STAGES.indexOf(application.status);

  const renderReviewerActions = () => {
    switch (application.status) {
      case 'FORMALITY_EXAMINATION':
        return (
          <>
            <button onClick={() => handleStatusUpdate('APPLICATION_PUBLICATION')}>Pass Formality Check</button>
            <div style={{ marginTop: '1rem' }}>
              <textarea
                placeholder="Provide reason for rejection"
                value={rejectionReason}
                onChange={(e) => setRejectionReason(e.target.value)}
                rows="3"
                style={{ width: '100%', marginBottom: '0.5rem' }}
              />
              <button
                onClick={() => handleStatusUpdate('DRAFTING_AND_FILING', rejectionReason)}
                disabled={!rejectionReason}
                className="button-danger"
              >
                Return to User for Correction
              </button>
            </div>
          </>
        );
      case 'APPLICATION_PUBLICATION':
        return <button onClick={() => handleStatusUpdate('SUBSTANTIVE_EXAMINATION_REQUEST')}>Publish Application</button>;
      case 'SUBSTANTIVE_EXAMINATION':
        return (
          <>
            <button onClick={() => handleStatusUpdate('FEE_PAYMENT_AND_GRANT')}>Pass Substantive Examination</button>
            <button onClick={() => handleStatusUpdate('DRAFTING_AND_FILING', 'Failed substantive examination.')} className="button-danger">
              Reject Application
            </button>
          </>
        );
      default:
        return <p>No actions available at this stage.</p>;
    }
  };

  const renderUserActions = () => {
    switch (application.status) {
      case 'DRAFTING_AND_FILING':
        return <button onClick={() => handleStatusUpdate('FORMALITY_EXAMINATION')}>Submit for Examination</button>;
      case 'SUBSTANTIVE_EXAMINATION_REQUEST':
        return <button onClick={() => handleStatusUpdate('SUBSTANTIVE_EXAMINATION')}>Activate Substantive Examination</button>;
      case 'FEE_PAYMENT_AND_GRANT':
        return <button>Pay Fee & Receive Patent</button>; // Placeholder
      default:
        return null;
    }
  };

  return (
    <div style={detailContainerStyle}>
      <div className="back-link-container">
        <button onClick={() => navigate(-1)} className="back-link" style={{ background: 'none', border: 'none', cursor: 'pointer', padding: 0 }}>
          ← Back to List
        </button>
      </div>
      <div style={detailStyle}>
        <h2>Application Details</h2>

        <div style={sectionStyle}>
          <h3>Workflow</h3>
          <div className="workflow-container">
            {STAGES.map((stage, index) => (
              <div key={stage} className={`workflow-stage ${index === currentStatusIndex ? 'active' : ''} ${index < currentStatusIndex ? 'completed' : ''}`}>
                <div className="workflow-dot"></div>
                <div className="workflow-label">{formatStatus(stage)}</div>
              </div>
            ))}
          </div>
        </div>
        
        <div style={sectionStyle}>
          <p><strong>ID:</strong> {application.id}</p>
          <p><strong>Title:</strong> {application.title}</p>
          <p><strong>Description:</strong> {application.description}</p>
          <p><strong>Submitted By:</strong> {application.submittedByUsername}</p>
          <p><strong>Current Status:</strong> <strong>{formatStatus(application.status)}</strong></p>
          {application.rejectionReason && (
            <p style={{ color: 'red' }}><strong>Rejection Reason:</strong> {application.rejectionReason}</p>
          )}
        </div>

        {application.files?.length > 0 && (
          <div style={sectionStyle}>
            <h3>Attached Files</h3>
            <ul style={fileListStyle}>
              {application.files.map((file) => (
                <li key={file.id} style={fileListItemStyle}>
                  <span>{file.fileName}</span>
                  <button onClick={() => handleFileDownload(file)}>Download</button>
                </li>
              ))}
            </ul>
          </div>
        )}

        {statusChangeError && <p style={{ color: 'red' }}>{statusChangeError}</p>}

        {isReviewer && !isOwner && (
          <div style={sectionStyle}>
            <h3>Reviewer Actions</h3>
            {renderReviewerActions()}
          </div>
        )}

        {isOwner && (
          <div style={sectionStyle}>
            <h3>My Actions</h3>
            {renderUserActions()}
          </div>
        )}
      </div>
    </div>
  );
};

export default ApplicationDetail;
