import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import ApplicationForm from './components/ApplicationForm';
import ApplicationList from './components/ApplicationList';
import ApplicationDetail from './components/ApplicationDetail';
import AmendmentPage from './components/AmendmentPage';
import PaymentPage from './components/PaymentPage';
import ProtectedRoute from './components/ProtectedRoute';
import Header from './components/Header';
import './styles/App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  const handleLogin = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
    setUser(userData);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  if (loading) {
    return <p>Loading...</p>; // Or a proper spinner component
  }

  return (
    <>
      {user && <Header user={user} onLogout={handleLogout} />}
      <div className="container">
        <Routes>
          {/* Public Routes */}
          <Route path="/login" element={!user ? <LoginForm onLogin={handleLogin} /> : <Navigate to="/applications" />} />
          <Route path="/register" element={!user ? <RegisterForm /> : <Navigate to="/applications" />} />

          {/* Protected Routes */}
          <Route element={<ProtectedRoute user={user} allowedRoles={['USER', 'REVIEWER']} />}>
            <Route path="/applications" element={<ApplicationList user={user} />} />
            <Route path="/applications/new" element={<ApplicationForm user={user} />} />
            <Route path="/applications/:id" element={<ApplicationDetail user={user} />} />
            <Route path="/applications/:id/amend" element={<AmendmentPage user={user} />} />
            <Route path="/applications/:id/payment" element={<PaymentPage user={user} />} />
          </Route>

          {/* Default Route */}
          <Route path="/" element={<Navigate to={user ? '/applications' : '/login'} />} />
          
          {/* Fallback for any other route */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </>
  );
}

export default App;
